package week1.day2;

public class ArithmeticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=60;
		int b=30;
		int c;
		
		c=a+b;
		System.out.println("sum of 2 nos:"+c);
		c=a-b;
		System.out.println("sub of 2 nos:"+c);
		c=a/b;
		System.out.println("div of 2 nos:"+c);
		c=a*b;
		System.out.println("mul of 2 nos:"+c);
		c=a%b;
		System.out.println("mod of 2 nos:"+c);
		c=--a;
		System.out.println("predec of a:"+c);
		c=++a;
		System.out.println("preinc of a:"+c);
		c=a++;
		System.out.println("postinc of a:"+c);
		c=a--;
		System.out.println("postdec of a:"+c);
		
	}

}
